        public static async ValueTask UpdateRecords(DirectoryJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Довідники.Організації_Select Організації_Select = new();
            Організації_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                /*Код*/ Довідники.Організації_Const.Код,
                /*Назва*/ Довідники.Організації_Const.Назва,
                
            ]);

            
                /* Сортування */
                Організації_Select.QuerySelect.Order.Add(
                Довідники.Організації_Const.Назва, SelectOrder.ASC);
            

            /* Відбори */
            Організації_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await Організації_Select.Select();
            while (Організації_Select.MoveNext())
            {
                Довідники.Організації_Pointer? curr = Організації_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DirectoryRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"] };
                    row.Fields.Add("Код", Fields[Організації_Const.Код].ToString() ?? "");
                    row.Fields.Add("Назва", Fields[Організації_Const.Назва].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

        public static async ValueTask LoadRecords(DirectoryJournalBase form)
        {
            form.BeforeLoadRecords();
            UnigueID? unigueIDSelect = form.SelectPointerItem ?? form.DirectoryPointerItem;

            /* Вибірка */
            Довідники.Організації_Select Організації_Select = new();
            Організації_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                /*Код*/ Довідники.Організації_Const.Код,
                /*Назва*/ Довідники.Організації_Const.Назва,
                
            ]);

            
                /* Сортування */
                Організації_Select.QuerySelect.Order.Add(
                Довідники.Організації_Const.Назва, SelectOrder.ASC);
            

            /* Відбори */
            if (form.WhereList != null) Організації_Select.QuerySelect.Where.AddRange(form.WhereList);

            /* Cторінки */
            await form.SplitPages(Організації_Select.SplitSelectToPages, Організації_Select.QuerySelect, unigueIDSelect);

            
            await Організації_Select.Select();
            form.Store.RemoveAll();
            uint selectPosition = 0;
            while (Організації_Select.MoveNext())
            {
                Довідники.Організації_Pointer? curr = Організації_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DirectoryRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"] };
                    row.Fields.Add("Код", Fields[Організації_Const.Код].ToString() ?? "");
                    row.Fields.Add("Назва", Fields[Організації_Const.Назва].ToString() ?? "");
                    
                    form.Store.Append(row);
                    if (row.UnigueID.Equals(unigueIDSelect)) selectPosition = form.Store.GetNItems();
                }
            }
            form.AfterLoadRecords(selectPosition);
        }
    }
        
