                /* Приєднання */
                Довідники.Контрагенти_Pointer.GetJoin(ПрихіднийКасовийОрдер_Select.QuerySelect, Документи.ПрихіднийКасовийОрдер_Const.Контрагент,
                ПрихіднийКасовийОрдер_Select.QuerySelect.Table, "join_tab_4", "Контрагент");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(ПрихіднийКасовийОрдер_Select.QuerySelect, Документи.ПрихіднийКасовийОрдер_Const.Автор,
                ПрихіднийКасовийОрдер_Select.QuerySelect.Table, "join_tab_5", "Автор");
            

            /* Відбори */
            ПрихіднийКасовийОрдер_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await ПрихіднийКасовийОрдер_Select.Select();
            while (ПрихіднийКасовийОрдер_Select.MoveNext())
            {
                Документи.ПрихіднийКасовийОрдер_Pointer? curr = ПрихіднийКасовийОрдер_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[ПрихіднийКасовийОрдер_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[ПрихіднийКасовийОрдер_Const.НомерДок].ToString() ?? "");
