            ]);

            
                /* Сортування */
                РозхіднийКасовийОрдер_Select.QuerySelect.Order.Add(
                Документи.РозхіднийКасовийОрдер_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(РозхіднийКасовийОрдер_Select.QuerySelect, Документи.РозхіднийКасовийОрдер_Const.Організація,
                РозхіднийКасовийОрдер_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Контрагенти_Pointer.GetJoin(РозхіднийКасовийОрдер_Select.QuerySelect, Документи.РозхіднийКасовийОрдер_Const.Контрагент,
                РозхіднийКасовийОрдер_Select.QuerySelect.Table, "join_tab_2", "Контрагент");
            
                /* Приєднання */
                Довідники.Валюти_Pointer.GetJoin(РозхіднийКасовийОрдер_Select.QuerySelect, Документи.РозхіднийКасовийОрдер_Const.Валюта,
                РозхіднийКасовийОрдер_Select.QuerySelect.Table, "join_tab_3", "Валюта");
            
                /* Приєднання */
                Довідники.Каси_Pointer.GetJoin(РозхіднийКасовийОрдер_Select.QuerySelect, Документи.РозхіднийКасовийОрдер_Const.Каса,
                РозхіднийКасовийОрдер_Select.QuerySelect.Table, "join_tab_4", "Каса");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(РозхіднийКасовийОрдер_Select.QuerySelect, Документи.РозхіднийКасовийОрдер_Const.Автор,
                РозхіднийКасовийОрдер_Select.QuerySelect.Table, "join_tab_5", "Автор");
            

            /* Відбори */
            РозхіднийКасовийОрдер_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await РозхіднийКасовийОрдер_Select.Select();
            while (РозхіднийКасовийОрдер_Select.MoveNext())
            {
                Документи.РозхіднийКасовийОрдер_Pointer? curr = РозхіднийКасовийОрдер_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[РозхіднийКасовийОрдер_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[РозхіднийКасовийОрдер_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[РозхіднийКасовийОрдер_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Контрагент", Fields["Контрагент"].ToString() ?? "");
                    row.Fields.Add("Валюта", Fields["Валюта"].ToString() ?? "");
                    row.Fields.Add("Каса", Fields["Каса"].ToString() ?? "");
                    row.Fields.Add("СумаДокументу", Fields[РозхіднийКасовийОрдер_Const.СумаДокументу].ToString() ?? "");
                    row.Fields.Add("ГосподарськаОперація", Перелічення.ПсевдонімиПерелічення.ГосподарськіОперації_Alias((Перелічення.ГосподарськіОперації)(Fields[РозхіднийКасовийОрдер_Const.ГосподарськаОперація] != DBNull.Value ? Fields[РозхіднийКасовийОрдер_Const.ГосподарськаОперація] : 0) ));
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[РозхіднийКасовийОрдер_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

