        public static async ValueTask UpdateRecords(DocumentJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Документи.ПереміщенняТоварів_Select ПереміщенняТоварів_Select = new();
            ПереміщенняТоварів_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.ПереміщенняТоварів_Const.Назва,
                /*НомерДок*/ Документи.ПереміщенняТоварів_Const.НомерДок,
                /*ДатаДок*/ Документи.ПереміщенняТоварів_Const.ДатаДок,
                /*Коментар*/ Документи.ПереміщенняТоварів_Const.Коментар,
                
            ]);

            
                /* Сортування */
                ПереміщенняТоварів_Select.QuerySelect.Order.Add(
                Документи.ПереміщенняТоварів_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(ПереміщенняТоварів_Select.QuerySelect, Документи.ПереміщенняТоварів_Const.Організація,
                ПереміщенняТоварів_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(ПереміщенняТоварів_Select.QuerySelect, Документи.ПереміщенняТоварів_Const.СкладВідправник,
                ПереміщенняТоварів_Select.QuerySelect.Table, "join_tab_2", "СкладВідправник");
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(ПереміщенняТоварів_Select.QuerySelect, Документи.ПереміщенняТоварів_Const.СкладОтримувач,
                ПереміщенняТоварів_Select.QuerySelect.Table, "join_tab_3", "СкладОтримувач");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(ПереміщенняТоварів_Select.QuerySelect, Документи.ПереміщенняТоварів_Const.Автор,
                ПереміщенняТоварів_Select.QuerySelect.Table, "join_tab_4", "Автор");
            

            /* Відбори */
            ПереміщенняТоварів_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await ПереміщенняТоварів_Select.Select();
            while (ПереміщенняТоварів_Select.MoveNext())
            {
                Документи.ПереміщенняТоварів_Pointer? curr = ПереміщенняТоварів_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[ПереміщенняТоварів_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[ПереміщенняТоварів_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[ПереміщенняТоварів_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("СкладВідправник", Fields["СкладВідправник"].ToString() ?? "");
                    row.Fields.Add("СкладОтримувач", Fields["СкладОтримувач"].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[ПереміщенняТоварів_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

