        public static void CreateFilter(DocumentJournalBase form)
        {
            
            List<FilterControl.FilterListItem> filterList = [];
            
            { /* СумаДокументу, numeric */
                Switch sw = Switch.New();
                
                        NumericControl СумаДокументу = new();
                        object get() => СумаДокументу.Value;
                    
                filterList.Add(new(АктВиконанихРобіт_Const.СумаДокументу, get, sw));
                form.Filter.Append("Сума:", СумаДокументу, sw);
            }
            
            form.Filter.GetWhere = () =>
            {
                List<Where> where = [];
                foreach (var filter in filterList)
                    if (filter.IsOn.Active)
                        where.Add(new Where(filter.Field, Comparison.EQ, filter.GetValueFunc.Invoke()));

                form.WhereList = where;
                return where.Count != 0;
            };
        
        }

        public static async ValueTask UpdateRecords(DocumentJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Документи.АктВиконанихРобіт_Select АктВиконанихРобіт_Select = new();
            АктВиконанихРобіт_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.АктВиконанихРобіт_Const.Назва,
                /*НомерДок*/ Документи.АктВиконанихРобіт_Const.НомерДок,
                /*ДатаДок*/ Документи.АктВиконанихРобіт_Const.ДатаДок,
                /*СумаДокументу*/ Документи.АктВиконанихРобіт_Const.СумаДокументу,
                /*Коментар*/ Документи.АктВиконанихРобіт_Const.Коментар,
                
            ]);

            
                /* Сортування */
                АктВиконанихРобіт_Select.QuerySelect.Order.Add(
                Документи.АктВиконанихРобіт_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(АктВиконанихРобіт_Select.QuerySelect, Документи.АктВиконанихРобіт_Const.Організація,
                АктВиконанихРобіт_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Валюти_Pointer.GetJoin(АктВиконанихРобіт_Select.QuerySelect, Документи.АктВиконанихРобіт_Const.Валюта,
                АктВиконанихРобіт_Select.QuerySelect.Table, "join_tab_2", "Валюта");
            
                /* Приєднання */
                Довідники.Каси_Pointer.GetJoin(АктВиконанихРобіт_Select.QuerySelect, Документи.АктВиконанихРобіт_Const.Каса,
                АктВиконанихРобіт_Select.QuerySelect.Table, "join_tab_3", "Каса");
            
                /* Приєднання */
                Довідники.Контрагенти_Pointer.GetJoin(АктВиконанихРобіт_Select.QuerySelect, Документи.АктВиконанихРобіт_Const.Контрагент,
                АктВиконанихРобіт_Select.QuerySelect.Table, "join_tab_4", "Контрагент");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(АктВиконанихРобіт_Select.QuerySelect, Документи.АктВиконанихРобіт_Const.Автор,
                АктВиконанихРобіт_Select.QuerySelect.Table, "join_tab_5", "Автор");
            

            /* Відбори */
            АктВиконанихРобіт_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await АктВиконанихРобіт_Select.Select();
            while (АктВиконанихРобіт_Select.MoveNext())
            {
                Документи.АктВиконанихРобіт_Pointer? curr = АктВиконанихРобіт_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[АктВиконанихРобіт_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[АктВиконанихРобіт_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[АктВиконанихРобіт_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Валюта", Fields["Валюта"].ToString() ?? "");
                    row.Fields.Add("Каса", Fields["Каса"].ToString() ?? "");
                    row.Fields.Add("Контрагент", Fields["Контрагент"].ToString() ?? "");
                    row.Fields.Add("СумаДокументу", Fields[АктВиконанихРобіт_Const.СумаДокументу].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[АктВиконанихРобіт_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

