        public static async ValueTask UpdateRecords(DocumentJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Документи.ПересортицяТоварів_Select ПересортицяТоварів_Select = new();
            ПересортицяТоварів_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.ПересортицяТоварів_Const.Назва,
                /*НомерДок*/ Документи.ПересортицяТоварів_Const.НомерДок,
                /*ДатаДок*/ Документи.ПересортицяТоварів_Const.ДатаДок,
                /*Коментар*/ Документи.ПересортицяТоварів_Const.Коментар,
                
            ]);

            
                /* Сортування */
                ПересортицяТоварів_Select.QuerySelect.Order.Add(
                Документи.ПересортицяТоварів_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(ПересортицяТоварів_Select.QuerySelect, Документи.ПересортицяТоварів_Const.Організація,
                ПересортицяТоварів_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(ПересортицяТоварів_Select.QuerySelect, Документи.ПересортицяТоварів_Const.Склад,
                ПересортицяТоварів_Select.QuerySelect.Table, "join_tab_2", "Склад");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(ПересортицяТоварів_Select.QuerySelect, Документи.ПересортицяТоварів_Const.Автор,
                ПересортицяТоварів_Select.QuerySelect.Table, "join_tab_3", "Автор");
            

            /* Відбори */
            ПересортицяТоварів_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await ПересортицяТоварів_Select.Select();
            while (ПересортицяТоварів_Select.MoveNext())
            {
                Документи.ПересортицяТоварів_Pointer? curr = ПересортицяТоварів_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[ПересортицяТоварів_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[ПересортицяТоварів_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[ПересортицяТоварів_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Склад", Fields["Склад"].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[ПересортицяТоварів_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

