        public static async ValueTask UpdateRecords(DocumentJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Документи.ПерерахунокТоварів_Select ПерерахунокТоварів_Select = new();
            ПерерахунокТоварів_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.ПерерахунокТоварів_Const.Назва,
                /*НомерДок*/ Документи.ПерерахунокТоварів_Const.НомерДок,
                /*ДатаДок*/ Документи.ПерерахунокТоварів_Const.ДатаДок,
                /*Коментар*/ Документи.ПерерахунокТоварів_Const.Коментар,
                
            ]);

            
                /* Сортування */
                ПерерахунокТоварів_Select.QuerySelect.Order.Add(
                Документи.ПерерахунокТоварів_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(ПерерахунокТоварів_Select.QuerySelect, Документи.ПерерахунокТоварів_Const.Організація,
                ПерерахунокТоварів_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(ПерерахунокТоварів_Select.QuerySelect, Документи.ПерерахунокТоварів_Const.Склад,
                ПерерахунокТоварів_Select.QuerySelect.Table, "join_tab_2", "Склад");
            
                /* Приєднання */
                Довідники.ФізичніОсоби_Pointer.GetJoin(ПерерахунокТоварів_Select.QuerySelect, Документи.ПерерахунокТоварів_Const.Відповідальний,
                ПерерахунокТоварів_Select.QuerySelect.Table, "join_tab_3", "Відповідальний");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(ПерерахунокТоварів_Select.QuerySelect, Документи.ПерерахунокТоварів_Const.Автор,
                ПерерахунокТоварів_Select.QuerySelect.Table, "join_tab_4", "Автор");
            

            /* Відбори */
            ПерерахунокТоварів_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await ПерерахунокТоварів_Select.Select();
            while (ПерерахунокТоварів_Select.MoveNext())
            {
                Документи.ПерерахунокТоварів_Pointer? curr = ПерерахунокТоварів_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[ПерерахунокТоварів_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[ПерерахунокТоварів_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[ПерерахунокТоварів_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Склад", Fields["Склад"].ToString() ?? "");
                    row.Fields.Add("Відповідальний", Fields["Відповідальний"].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[ПерерахунокТоварів_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

