        public static void CreateFilter(DocumentJournalBase form)
        {
            
            List<FilterControl.FilterListItem> filterList = [];
            
            { /* СумаДокументу, numeric */
                Switch sw = Switch.New();
                
                        NumericControl СумаДокументу = new();
                        object get() => СумаДокументу.Value;
                    
                filterList.Add(new(РахунокФактура_Const.СумаДокументу, get, sw));
                form.Filter.Append("Сума:", СумаДокументу, sw);
            }
            
            form.Filter.GetWhere = () =>
            {
                List<Where> where = [];
                foreach (var filter in filterList)
                    if (filter.IsOn.Active)
                        where.Add(new Where(filter.Field, Comparison.EQ, filter.GetValueFunc.Invoke()));

                form.WhereList = where;
                return where.Count != 0;
            };
        
        }

        public static async ValueTask UpdateRecords(DocumentJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Документи.РахунокФактура_Select РахунокФактура_Select = new();
            РахунокФактура_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.РахунокФактура_Const.Назва,
                /*НомерДок*/ Документи.РахунокФактура_Const.НомерДок,
                /*ДатаДок*/ Документи.РахунокФактура_Const.ДатаДок,
                /*СумаДокументу*/ Документи.РахунокФактура_Const.СумаДокументу,
                /*Коментар*/ Документи.РахунокФактура_Const.Коментар,
                
            ]);

            
                /* Сортування */
                РахунокФактура_Select.QuerySelect.Order.Add(
                Документи.РахунокФактура_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(РахунокФактура_Select.QuerySelect, Документи.РахунокФактура_Const.Організація,
                РахунокФактура_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Контрагенти_Pointer.GetJoin(РахунокФактура_Select.QuerySelect, Документи.РахунокФактура_Const.Контрагент,
                РахунокФактура_Select.QuerySelect.Table, "join_tab_2", "Контрагент");
            
                /* Приєднання */
                Довідники.Валюти_Pointer.GetJoin(РахунокФактура_Select.QuerySelect, Документи.РахунокФактура_Const.Валюта,
                РахунокФактура_Select.QuerySelect.Table, "join_tab_3", "Валюта");
            
                /* Приєднання */
                Довідники.Каси_Pointer.GetJoin(РахунокФактура_Select.QuerySelect, Документи.РахунокФактура_Const.Каса,
                РахунокФактура_Select.QuerySelect.Table, "join_tab_4", "Каса");
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(РахунокФактура_Select.QuerySelect, Документи.РахунокФактура_Const.Склад,
                РахунокФактура_Select.QuerySelect.Table, "join_tab_5", "Склад");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(РахунокФактура_Select.QuerySelect, Документи.РахунокФактура_Const.Автор,
                РахунокФактура_Select.QuerySelect.Table, "join_tab_6", "Автор");
            

            /* Відбори */
            РахунокФактура_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await РахунокФактура_Select.Select();
            while (РахунокФактура_Select.MoveNext())
            {
                Документи.РахунокФактура_Pointer? curr = РахунокФактура_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[РахунокФактура_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[РахунокФактура_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[РахунокФактура_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Контрагент", Fields["Контрагент"].ToString() ?? "");
                    row.Fields.Add("Валюта", Fields["Валюта"].ToString() ?? "");
                    row.Fields.Add("Каса", Fields["Каса"].ToString() ?? "");
                    row.Fields.Add("Склад", Fields["Склад"].ToString() ?? "");
                    row.Fields.Add("СумаДокументу", Fields[РахунокФактура_Const.СумаДокументу].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[РахунокФактура_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

