        public static async ValueTask UpdateRecords(DocumentJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Документи.РозміщенняТоварівНаСкладі_Select РозміщенняТоварівНаСкладі_Select = new();
            РозміщенняТоварівНаСкладі_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.РозміщенняТоварівНаСкладі_Const.Назва,
                /*ДатаДок*/ Документи.РозміщенняТоварівНаСкладі_Const.ДатаДок,
                /*НомерДок*/ Документи.РозміщенняТоварівНаСкладі_Const.НомерДок,
                /*Коментар*/ Документи.РозміщенняТоварівНаСкладі_Const.Коментар,
                
            ]);

            
                /* Сортування */
                РозміщенняТоварівНаСкладі_Select.QuerySelect.Order.Add(
                Документи.РозміщенняТоварівНаСкладі_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(РозміщенняТоварівНаСкладі_Select.QuerySelect, Документи.РозміщенняТоварівНаСкладі_Const.Склад,
                РозміщенняТоварівНаСкладі_Select.QuerySelect.Table, "join_tab_1", "Склад");
            
                /* Приєднання */
                Документи.ПоступленняТоварівТаПослуг_Pointer.GetJoin(РозміщенняТоварівНаСкладі_Select.QuerySelect, Документи.РозміщенняТоварівНаСкладі_Const.ДокументПоступлення,
                РозміщенняТоварівНаСкладі_Select.QuerySelect.Table, "join_tab_2", "ДокументПоступлення");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(РозміщенняТоварівНаСкладі_Select.QuerySelect, Документи.РозміщенняТоварівНаСкладі_Const.Автор,
                РозміщенняТоварівНаСкладі_Select.QuerySelect.Table, "join_tab_3", "Автор");
            

            /* Відбори */
            РозміщенняТоварівНаСкладі_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await РозміщенняТоварівНаСкладі_Select.Select();
            while (РозміщенняТоварівНаСкладі_Select.MoveNext())
            {
                Документи.РозміщенняТоварівНаСкладі_Pointer? curr = РозміщенняТоварівНаСкладі_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[РозміщенняТоварівНаСкладі_Const.Назва].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[РозміщенняТоварівНаСкладі_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[РозміщенняТоварівНаСкладі_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("Склад", Fields["Склад"].ToString() ?? "");
                    row.Fields.Add("ДокументПоступлення", Fields["ДокументПоступлення"].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[РозміщенняТоварівНаСкладі_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

