                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(ПереміщенняТоварівНаСкладі_Select.QuerySelect, Документи.ПереміщенняТоварівНаСкладі_Const.Склад,
                ПереміщенняТоварівНаСкладі_Select.QuerySelect.Table, "join_tab_1", "Склад");
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(ПереміщенняТоварівНаСкладі_Select.QuerySelect, Документи.ПереміщенняТоварівНаСкладі_Const.Організація,
                ПереміщенняТоварівНаСкладі_Select.QuerySelect.Table, "join_tab_2", "Організація");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(ПереміщенняТоварівНаСкладі_Select.QuerySelect, Документи.ПереміщенняТоварівНаСкладі_Const.Автор,
                ПереміщенняТоварівНаСкладі_Select.QuerySelect.Table, "join_tab_3", "Автор");
            

            /* Відбори */
            ПереміщенняТоварівНаСкладі_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await ПереміщенняТоварівНаСкладі_Select.Select();
            while (ПереміщенняТоварівНаСкладі_Select.MoveNext())
            {
                Документи.ПереміщенняТоварівНаСкладі_Pointer? curr = ПереміщенняТоварівНаСкладі_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[ПереміщенняТоварівНаСкладі_Const.Назва].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[ПереміщенняТоварівНаСкладі_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[ПереміщенняТоварівНаСкладі_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("Склад", Fields["Склад"].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[ПереміщенняТоварівНаСкладі_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

