        public static async ValueTask UpdateRecords(DocumentJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Документи.КорегуванняБоргу_Select КорегуванняБоргу_Select = new();
            КорегуванняБоргу_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.КорегуванняБоргу_Const.Назва,
                /*НомерДок*/ Документи.КорегуванняБоргу_Const.НомерДок,
                /*ДатаДок*/ Документи.КорегуванняБоргу_Const.ДатаДок,
                /*Коментар*/ Документи.КорегуванняБоргу_Const.Коментар,
                
            ]);

            
                /* Сортування */
                КорегуванняБоргу_Select.QuerySelect.Order.Add(
                Документи.КорегуванняБоргу_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(КорегуванняБоргу_Select.QuerySelect, Документи.КорегуванняБоргу_Const.Організація,
                КорегуванняБоргу_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(КорегуванняБоргу_Select.QuerySelect, Документи.КорегуванняБоргу_Const.Автор,
                КорегуванняБоргу_Select.QuerySelect.Table, "join_tab_2", "Автор");
            

            /* Відбори */
            КорегуванняБоргу_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await КорегуванняБоргу_Select.Select();
            while (КорегуванняБоргу_Select.MoveNext())
            {
                Документи.КорегуванняБоргу_Pointer? curr = КорегуванняБоргу_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[КорегуванняБоргу_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[КорегуванняБоргу_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[КорегуванняБоргу_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[КорегуванняБоргу_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

        public static async ValueTask LoadRecords(DocumentJournalBase form)
        {
            form.BeforeLoadRecords();
            UnigueID? unigueIDSelect = form.SelectPointerItem ?? form.DocumentPointerItem;

            /* Вибірка */
            Документи.КорегуванняБоргу_Select КорегуванняБоргу_Select = new();
            КорегуванняБоргу_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.КорегуванняБоргу_Const.Назва,
                /*НомерДок*/ Документи.КорегуванняБоргу_Const.НомерДок,
                /*ДатаДок*/ Документи.КорегуванняБоргу_Const.ДатаДок,
                /*Коментар*/ Документи.КорегуванняБоргу_Const.Коментар,
                
            ]);

            
                /* Сортування */
                КорегуванняБоргу_Select.QuerySelect.Order.Add(
                Документи.КорегуванняБоргу_Const.ДатаДок, SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(КорегуванняБоргу_Select.QuerySelect, Документи.КорегуванняБоргу_Const.Організація,
                КорегуванняБоргу_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(КорегуванняБоргу_Select.QuerySelect, Документи.КорегуванняБоргу_Const.Автор,
                КорегуванняБоргу_Select.QuerySelect.Table, "join_tab_2", "Автор");
            

            /* Відбори */
            if (form.WhereList != null) КорегуванняБоргу_Select.QuerySelect.Where.AddRange(form.WhereList);

            /* Відбір за період */
             if (form.TypeWhereState == InterfaceGtk4.FormJournal.TypeWhere.Standart || (form.TypeWhereState == InterfaceGtk4.FormJournal.TypeWhere.Filter && form.Filter.IsUsePeriod))
            {
                Where? where = InterfaceGtk4.PeriodForJournal.ВідбірПоПеріоду(Документи.КорегуванняБоргу_Const.ДатаДок, form.Period.Period, form.Period.DateStart, form.Period.DateStop);
                if (where != null) КорегуванняБоргу_Select.QuerySelect.Where.Add(where);
            }

            /* Cторінки */
            await form.SplitPages(КорегуванняБоргу_Select.SplitSelectToPages, КорегуванняБоргу_Select.QuerySelect, unigueIDSelect);

            
            await КорегуванняБоргу_Select.Select();
            form.Store.RemoveAll();
            uint selectPosition = 0;
            while (КорегуванняБоргу_Select.MoveNext())
            {
                Документи.КорегуванняБоргу_Pointer? curr = КорегуванняБоргу_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[КорегуванняБоргу_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[КорегуванняБоргу_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[КорегуванняБоргу_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[КорегуванняБоргу_Const.Коментар].ToString() ?? "");
                    
                    form.Store.Append(row);
                    if (row.UnigueID.Equals(unigueIDSelect)) selectPosition = form.Store.GetNItems();
                }
            }
            form.AfterLoadRecords(selectPosition);
        }
    }
        
