        public static void CreateFilter(DocumentJournalBase form)
        {
            
            List<FilterControl.FilterListItem> filterList = [];
            
            { /* СумаДокументу, numeric */
                Switch sw = Switch.New();
                
                        NumericControl СумаДокументу = new();
                        object get() => СумаДокументу.Value;
                    
                filterList.Add(new(ЗакриттяЗамовленняКлієнта_Const.СумаДокументу, get, sw));
                form.Filter.Append("Сума:", СумаДокументу, sw);
            }
            
            { /* ПричинаЗакриттяЗамовлення, enum */
                Switch sw = Switch.New();
                
                        ComboBoxText ПричинаЗакриттяЗамовлення = new();
                        foreach (var item in ПсевдонімиПерелічення.ПричиниЗакриттяЗамовленняКлієнта_List())
                            ПричинаЗакриттяЗамовлення.Append(item.Value.ToString(), item.Name);
                        ПричинаЗакриттяЗамовлення.Active = 0;
                        object get() => Enum.TryParse(ПричинаЗакриттяЗамовлення.ActiveId, out ПричиниЗакриттяЗамовленняКлієнта value) ? (int)value: 0;
                    
                filterList.Add(new(ЗакриттяЗамовленняКлієнта_Const.ПричинаЗакриттяЗамовлення, get, sw));
                form.Filter.Append("Причина:", ПричинаЗакриттяЗамовлення, sw);
            }
            
            form.Filter.GetWhere = () =>
            {
                List<Where> where = [];
                foreach (var filter in filterList)
                    if (filter.IsOn.Active)
                        where.Add(new Where(filter.Field, Comparison.EQ, filter.GetValueFunc.Invoke()));

                form.WhereList = where;
                return where.Count != 0;
            };
        
        }

        public static async ValueTask UpdateRecords(DocumentJournalBase form)
