                Довідники.Валюти_Pointer.GetJoin(ЗакриттяЗамовленняКлієнта_Select.QuerySelect, Документи.ЗакриттяЗамовленняКлієнта_Const.Валюта,
                ЗакриттяЗамовленняКлієнта_Select.QuerySelect.Table, "join_tab_3", "Валюта");
            
                /* Приєднання */
                Довідники.Каси_Pointer.GetJoin(ЗакриттяЗамовленняКлієнта_Select.QuerySelect, Документи.ЗакриттяЗамовленняКлієнта_Const.Каса,
                ЗакриттяЗамовленняКлієнта_Select.QuerySelect.Table, "join_tab_4", "Каса");
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(ЗакриттяЗамовленняКлієнта_Select.QuerySelect, Документи.ЗакриттяЗамовленняКлієнта_Const.Склад,
                ЗакриттяЗамовленняКлієнта_Select.QuerySelect.Table, "join_tab_5", "Склад");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(ЗакриттяЗамовленняКлієнта_Select.QuerySelect, Документи.ЗакриттяЗамовленняКлієнта_Const.Автор,
                ЗакриттяЗамовленняКлієнта_Select.QuerySelect.Table, "join_tab_6", "Автор");
            

            /* Відбори */
            ЗакриттяЗамовленняКлієнта_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await ЗакриттяЗамовленняКлієнта_Select.Select();
            while (ЗакриттяЗамовленняКлієнта_Select.MoveNext())
            {
                Документи.ЗакриттяЗамовленняКлієнта_Pointer? curr = ЗакриттяЗамовленняКлієнта_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[ЗакриттяЗамовленняКлієнта_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[ЗакриттяЗамовленняКлієнта_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[ЗакриттяЗамовленняКлієнта_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Контрагент", Fields["Контрагент"].ToString() ?? "");
                    row.Fields.Add("Валюта", Fields["Валюта"].ToString() ?? "");
                    row.Fields.Add("Каса", Fields["Каса"].ToString() ?? "");
                    row.Fields.Add("Склад", Fields["Склад"].ToString() ?? "");
                    row.Fields.Add("СумаДокументу", Fields[ЗакриттяЗамовленняКлієнта_Const.СумаДокументу].ToString() ?? "");
                    row.Fields.Add("ПричинаЗакриттяЗамовлення", Перелічення.ПсевдонімиПерелічення.ПричиниЗакриттяЗамовленняКлієнта_Alias((Перелічення.ПричиниЗакриттяЗамовленняКлієнта)(Fields[ЗакриттяЗамовленняКлієнта_Const.ПричинаЗакриттяЗамовлення] != DBNull.Value ? Fields[ЗакриттяЗамовленняКлієнта_Const.ПричинаЗакриттяЗамовлення] : 0) ));
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[ЗакриттяЗамовленняКлієнта_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

