                /* Приєднання */
                Довідники.Каси_Pointer.GetJoin(ЗакриттяРахункуФактури_Select.QuerySelect, Документи.ЗакриттяРахункуФактури_Const.Каса,
                ЗакриттяРахункуФактури_Select.QuerySelect.Table, "join_tab_4", "Каса");
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(ЗакриттяРахункуФактури_Select.QuerySelect, Документи.ЗакриттяРахункуФактури_Const.Склад,
                ЗакриттяРахункуФактури_Select.QuerySelect.Table, "join_tab_5", "Склад");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(ЗакриттяРахункуФактури_Select.QuerySelect, Документи.ЗакриттяРахункуФактури_Const.Автор,
                ЗакриттяРахункуФактури_Select.QuerySelect.Table, "join_tab_6", "Автор");
            

            /* Відбори */
            ЗакриттяРахункуФактури_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await ЗакриттяРахункуФактури_Select.Select();
            while (ЗакриттяРахункуФактури_Select.MoveNext())
            {
                Документи.ЗакриттяРахункуФактури_Pointer? curr = ЗакриттяРахункуФактури_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[ЗакриттяРахункуФактури_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[ЗакриттяРахункуФактури_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[ЗакриттяРахункуФактури_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Контрагент", Fields["Контрагент"].ToString() ?? "");
                    row.Fields.Add("Валюта", Fields["Валюта"].ToString() ?? "");
                    row.Fields.Add("Каса", Fields["Каса"].ToString() ?? "");
                    row.Fields.Add("Склад", Fields["Склад"].ToString() ?? "");
                    row.Fields.Add("СумаДокументу", Fields[ЗакриттяРахункуФактури_Const.СумаДокументу].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[ЗакриттяРахункуФактури_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

