        public static async ValueTask UpdateRecords(DocumentJournalBase form)
        {
            List<ObjectChanged> records = [];
            lock (form.Loсked)
            {
                while(form.RecordsChangedQueue.Count > 0)
                    records.AddRange(form.RecordsChangedQueue.Dequeue());
            }
            
            /* Вибірка */
            Документи.ЧекККМ_Select ЧекККМ_Select = new();
            ЧекККМ_Select.QuerySelect.Field.AddRange(
            [
                "deletion_label",
                "spend",
                /*Назва*/ Документи.ЧекККМ_Const.Назва,
                /*НомерДок*/ Документи.ЧекККМ_Const.НомерДок,
                /*ДатаДок*/ Документи.ЧекККМ_Const.ДатаДок,
                /*СумаБезЗнижки*/ Документи.ЧекККМ_Const.СумаБезЗнижки,
                /*Знижка*/ Документи.ЧекККМ_Const.Знижка,
                /*СумаДокументу*/ Документи.ЧекККМ_Const.СумаДокументу,
                /*Коментар*/ Документи.ЧекККМ_Const.Коментар,
                
            ]);

            
                /* Сортування */
                ЧекККМ_Select.QuerySelect.Order.Add(
                Документи.ЧекККМ_Const.ДатаДок, SelectOrder.ASC);
            
                /* Сортування */
                ЧекККМ_Select.QuerySelect.Order.Add(
                "Автор", SelectOrder.ASC);
            
                /* Приєднання */
                Довідники.Організації_Pointer.GetJoin(ЧекККМ_Select.QuerySelect, Документи.ЧекККМ_Const.Організація,
                ЧекККМ_Select.QuerySelect.Table, "join_tab_1", "Організація");
            
                /* Приєднання */
                Довідники.Валюти_Pointer.GetJoin(ЧекККМ_Select.QuerySelect, Документи.ЧекККМ_Const.Валюта,
                ЧекККМ_Select.QuerySelect.Table, "join_tab_2", "Валюта");
            
                /* Приєднання */
                Довідники.Склади_Pointer.GetJoin(ЧекККМ_Select.QuerySelect, Документи.ЧекККМ_Const.Склад,
                ЧекККМ_Select.QuerySelect.Table, "join_tab_3", "Склад");
            
                /* Приєднання */
                Довідники.Користувачі_Pointer.GetJoin(ЧекККМ_Select.QuerySelect, Документи.ЧекККМ_Const.Автор,
                ЧекККМ_Select.QuerySelect.Table, "join_tab_4", "Автор");
            
                /* Приєднання */
                Довідники.КасиККМ_Pointer.GetJoin(ЧекККМ_Select.QuerySelect, Документи.ЧекККМ_Const.КасаККМ,
                ЧекККМ_Select.QuerySelect.Table, "join_tab_5", "КасаККМ");
            

            /* Відбори */
            ЧекККМ_Select.QuerySelect.Where.Add(new Where("uid", Comparison.IN, "'" + string.Join("', '", records.Select(x => x.Uid)) + "'", true));

            
            await ЧекККМ_Select.Select();
            while (ЧекККМ_Select.MoveNext())
            {
                Документи.ЧекККМ_Pointer? curr = ЧекККМ_Select.Current;
                if (curr != null)
                {
                    Dictionary<string, object> Fields = curr.Fields;
                    DocumentRowJournal row = new() { UnigueID = curr.UnigueID, DeletionLabel = (bool)Fields["deletion_label"], Spend = (bool)Fields["spend"] };
                    row.Fields.Add("Назва", Fields[ЧекККМ_Const.Назва].ToString() ?? "");
                    row.Fields.Add("НомерДок", Fields[ЧекККМ_Const.НомерДок].ToString() ?? "");
                    row.Fields.Add("ДатаДок", Fields[ЧекККМ_Const.ДатаДок].ToString() ?? "");
                    row.Fields.Add("Організація", Fields["Організація"].ToString() ?? "");
                    row.Fields.Add("Валюта", Fields["Валюта"].ToString() ?? "");
                    row.Fields.Add("Склад", Fields["Склад"].ToString() ?? "");
                    row.Fields.Add("СумаБезЗнижки", Fields[ЧекККМ_Const.СумаБезЗнижки].ToString() ?? "");
                    row.Fields.Add("Знижка", Fields[ЧекККМ_Const.Знижка].ToString() ?? "");
                    row.Fields.Add("СумаДокументу", Fields[ЧекККМ_Const.СумаДокументу].ToString() ?? "");
                    row.Fields.Add("Автор", Fields["Автор"].ToString() ?? "");
                    row.Fields.Add("КасаККМ", Fields["КасаККМ"].ToString() ?? "");
                    row.Fields.Add("Коментар", Fields[ЧекККМ_Const.Коментар].ToString() ?? "");
                    
                    ObjectChanged? objCh = records.Find(x => x.Uid.Equals(curr.UnigueID.UGuid));
                    if (objCh != null)
                    {
                        bool exist = false;
                        for (uint i = 0; i < form.Store.GetNItems(); i++)
                        {
                            Row? item = (Row?)form.Store.GetObject(i);
                            if (item != null && item.UnigueID.Equals(curr.UnigueID))
                            {
                                bool sel = form.Grid.Model.IsSelected(i);
                                form.Store.Splice(i, 1, [row], 1);
                                if (sel) form.Grid.Model.SelectItem(i, false);
                                exist = true;
                                break;
                            }
                        }
                        if (!exist && objCh.Type == TypeObjectChanged.Add)
                            form.Store.Append(row);
                    }
                }
            }
        }

